import { useState } from "react";

const Login = () => {
    const [isLogin, setIsLogin] = useState(true);
    const convertContent = () => {
        if(isLogin){
            setIsLogin(false);
        } else{
            setIsLogin(true);
        }
    }
    return(
        <div className="Login">
            <div className="left_col">
                <div className="text_welcome">환영합니다!</div>
            </div>
            <div className="right_col">
                <div className="text_title">
                    {isLogin ? "로그인" : "회원가입"}
                </div>
                <div className="email_login">
                    이메일로 {isLogin ? "로그인" : "회원가입"}
                    <div className="by_email">
                        <input placeholder="이메일을 입력하세요."/>
                        <button className="btn_login">{isLogin ? "로그인" : "회원가입"}</button>
                    </div>
                </div>
                <div className="by_social">
                    소셜 계정으로 {isLogin ? "로그인" : "회원가입"}
                    <div className="buttons">
                        <button/>
                        <button/>
                        <button/>
                    </div>
                </div>
                <div className="go_signup">
                {isLogin ? "아직 회원이 아니신가요?" : "계정이 이미 있으신가요?"}
                    <button className="btn_signup" onClick={convertContent}>{isLogin ? "로그인" : "회원가입"}</button>
                </div>  
            </div>          
        </div>
    )
}

export default Login;
