import { useState } from "react";

const Login = () => {
    const [isLogin, setIsLogin] = useState(true);
    const convertContent = () => {
        if(isLogin){
            setIsLogin(false);
        } else{
            setIsLogin(true);
        }
    }
    return(
        <div className="Login">
            <div className="text_title">
                {isLogin ? 로그인 : 회원가입}
            </div>
            <div className="email_login">
                이메일로 {isLogin ? 로그인 : 회원가입}
                <div className="email_input">
                    <input placeholder="이메일을 입력하세요."/>
                    <button className="btn_login">{isLogin ? 로그인 : 회원가입}</button>
                </div>
            </div>
            <div className="social_login">
                소셜 계정으로 로그인
                <div className="buttons">
                    <button/>
                    <button/>
                    <button/>
                </div>
            </div>
            <div className="go_signup">
                아직 회원이 아니신가요?
                <button className="btn_signup" onClick={convertContent}>회원가입</button>
            </div>            
        </div>
    )
}

export default Login;
