import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const SignUp = () => {
    const code = new URL(window.location.href).searchParams.get("code");
    const navigate = useNavigate();

    const [userInfo, setUserInfo] = useState({
        name: "",
        email: "",
        userId: "",
        userIntro: "",
        isChecked: false,
    })
    const { name, email, userId, userIntro, isChecked } = userInfo;

    const isValidUserId = (email.length > 2 && email.length < 17);
    const getIsActive = isChecked && isValidUserId;
    const goBack = () => {
        navigate(`/`);
    }
    useEffect(() => {
        async function signUpEmail() {
            //
        };
        signUpEmail();
    })
    return (
        <div className="SignUp">
            <div className="title">
                <h1>환영합니다!</h1>
                <h5>기본 회원 정보를 등록해주세요.</h5>
            </div>
            <div className="container">
                <div className="input_name">
                    <h4>이름</h4>
                    <input placeholder="이름을 입력하세요" name="name"/>
                </div>
                <div className="input_email">
                    <h4>이메일</h4>
                    <input placeholder="" name="email"/>
                </div>
                <div className="input_userId">
                    <h4>아이디</h4>
                    <input placeholder="아이디를 입력하세요" name="userId"/>
                </div>
                <div className="input_intro">
                    <h4>한 줄 소개</h4>
                    <input placeholder="당신을 한 줄로 소개해보세요" name="intro"/>
                </div>
                <div className="checkbox">
                    <input type="checkbox"/>
                    <h5 className="text_green">이용약관</h5>
                    <h5>과</h5>
                    <h5 className="text_green">개인정보취급방침</h5>
                    <h5>에 동의합니다.</h5>
                </div>
            </div>
            <div className="footer_buttons">
                <button onClick={goBack}>취소</button>
                <button>다음</button>
            </div>
        </div>
    )
}

export default SignUp;
