import { useState } from "react";
import "../styles/Write.css";

const Write = () => {
    const [title, setTitle] = useState("");
    const [contents, setContents] = useState("");

    const handleChangeContents = (e) => {
        setContents(e.target.value);
    }
    return( 
        <div className="Write">
            <div className="left">
                <div className="wrapper">
                    <div className="title">
                        <textarea value={title} onChange={handleChangeContents} placeholder="제목을 입력하세요"></textarea>
                        <div className="line"></div>
                    </div>
                    {/* tags */}
                    {/* toolbar */}
                    <div className="contents">
                        <textarea value={contents} onChange={handleChangeContents} placeholder="당신의 이야기를 적어보세요..."></textarea>
                    </div>
                </div>
                <div className="buttons">
                    <div className="left_col">
                        <svg stroke="currentColor" fill="currentColor" strokeWidth="0" viewBox="0 0 24 24" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg"><path d="M20 11H7.83l5.59-5.59L12 4l-8 8 8 8 1.41-1.41L7.83 13H20v-2z"></path></svg>
                        <span>나가기</span>
                    </div>
                    <div className="right_col">
                        <button className="btn_temporaryStorage">임시저장</button>
                        <button className="btn_publish">출간하기</button>
                    </div>
                </div>
            </div>
            <div className="right">
                <div className="preview">
                    <h1 className="title">

                    </h1>
                    <div className="contents">

                    </div>
                </div>
            </div>
        </div>
    )
}

export default Write;
