import Modal from "react-modal";
import Login from "./Login";
import { useState } from "react";
import axios from "axios";
import "../styles/Main.css";
import icon_themeToggle from "../assets/header_themeToggle.png";
import icon_search from "../assets/header_search.png";

const Main = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    
    const closeModal = () => {
        setModalIsOpen(false);
    }
    const modalStyle = {
        content: {
            width: "290px",
            height: "240px",
            margin: "auto",
            padding: "10px",
            border: "none",
            borderRadius: "30px", 
            boxShadow: "0 0 5px 2px rgba(0, 0, 0, 0.1)",
        }
    }

    return(
        <div className="Main">
            <div className="container">
                <div className="header">
                    <div className="header_left">velog</div>
                    <div className="header_right">
                        <button className="header_icon">
                            <img alt="btn_toggle_theme" src={icon_themeToggle}/>
                        </button>
                        <button className="header_icon">
                            <img alt="btn_search" src={icon_search}/>
                        </button>
                        <button  className="btn_login" onClick={()=>{setModalIsOpen(true)}}>로그인</button>
                        <Modal style={modalStyle} ariaHideApp={false} isOpen={modalIsOpen} onRequestClose={closeModal}>
                            <button className="btn_close" onClick={closeModal}>x</button>
                            <Login />
                        </Modal>
                    </div>
                </div>
                <div className="homeTab_wrapper">
                    <div className="homeTab_left">
                    <svg stroke="currentColor" fill="currentColor" stroke-width="0" viewBox="0 0 24 24" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg"><path fill="none" d="M0 0h24v24H0z"></path><path d="M16 6l2.29 2.29-4.88 4.88-4-4L2 16.59 3.41 18l6-6 4 4 6.3-6.29L22 12V6z"></path></svg>
                    <span>트렌딩</span>
                    </div>
                <div className="homeTab_right">

                </div>
                </div>
            </div>
        </div>
    )
}

export default Main;
