import Modal from "react-modal";
import Login from "./Login";
import { useState } from "react";
import axios from "axios";

const server_url = `http://172.21.48.213:8080`;

const Main = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    
    const closeModal = () => {
        setModalIsOpen(false);
    }
    const modalStyle = {
        content: {
            width: "290px",
            height: "240px",
            margin: "auto",
            padding: "10px",
            border: "none",
            borderRadius: "30px", 
            boxShadow: "0 0 5px 2px rgba(0, 0, 0, 0.1)",
        }
    }
    const data = {
        title : "mockTitle",
		author : "mockAuthor",
		body : "UMC Server 화이팅!"
    }
    const post = () => {
        axios.post([server_url, `/api/posts/upload`].join(""), data)
        .then(()=>{
            console.log("posted");
        })
    }
    const get1 = () => {
        axios.get([server_url, `/api/posts/`].join(""))
        .then((res)=>{
            console.log(res.data);
        })
    }
    const get2 = () => {
        const id = "";
        axios.get([server_url, `/api/posts/`, id].join(""))
        .then((res)=>{
            console.log(res.data);
        })
    }

    return(
        <div className="Main">
            <div className="header">
                <div className="left_col">velog</div>
                <div className="right_col">
                    <button/>
                    <button/>
                    <button onClick={()=>{setModalIsOpen(true)}}>로그인</button>
                    <Modal style={modalStyle} ariaHideApp={false} isOpen={modalIsOpen} onRequestClose={closeModal}>
                        <button className="btn_close" onClick={closeModal}>x</button>
                        <Login />
                    </Modal>
                </div>
            </div>
            <button onClick={post}>POST</button>
            <button onClick={get1}>GET1</button>
            <button onClick={get2}>GET2</button>
        </div>
    )
}

export default Main;
