import Modal from "react-modal";
import Login from "./Login";
import { useState } from "react";
import axios from "axios";
import "../styles/Main.css";

const Main = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    
    const closeModal = () => {
        setModalIsOpen(false);
    }
    const modalStyle = {
        content: {
            width: "290px",
            height: "240px",
            margin: "auto",
            padding: "10px",
            border: "none",
            borderRadius: "30px", 
            boxShadow: "0 0 5px 2px rgba(0, 0, 0, 0.1)",
        }
    }

    return(
        <div className="Main">
            <div className="header">
                <div className="left_col">velog</div>
                <div className="right_col">
                    <button/>
                    <button/>
                    <button onClick={()=>{setModalIsOpen(true)}}>로그인</button>
                    <Modal style={modalStyle} ariaHideApp={false} isOpen={modalIsOpen} onRequestClose={closeModal}>
                        <button className="btn_close" onClick={closeModal}>x</button>
                        <Login />
                    </Modal>
                </div>
            </div>
        </div>
    )
}

export default Main;
